        'job_manually_triggered',
        req.user.id,
        null,
        'job',
        { job_name: jobName },
        req
      );

      res.json({
        success: true,
        message: `Job '${jobName}' triggered successfully`
      });
    } catch (error) {
      console.error('Trigger job error:', error);
      res.status(400).json({
