const Order = require('../models/order.model');
const Cart = require('../models/cart.model');
const User = require('../models/user.model');

class OrderService {
  static async createFromCart(userId, orderData) {
    // Get user's cart
    const cart = await Cart.getByUserId(userId);
    if (!cart || !cart.items || cart.items.length === 0) {
      throw new Error('Cart is empty');
    }

    // Validate cart before creating order
    const validation = await cart.validateForCheckout();
    if (!validation.valid) {
      throw new Error(`Cart validation failed: ${validation.issues.join(', ')}`);
    }

    // Prepare order data
    const items = cart.items.map((item) => ({
      productId: item.productId,
      quantity: item.quantity
    }));

    const order = await Order.create({
      buyerId: userId,
      items,
      ...orderData
    });

    // Clear cart after successful order creation
    await Cart.clear(userId);

    return order;
  }

  static async createOrder(buyerId, orderData) {
    // Verify buyer exists and is active
    const buyer = await User.findById(buyerId);
    if (!buyer || buyer.status !== 'active') {
      throw new Error('Buyer not found or account not active');
    }

    if (buyer.role !== 'buyer') {
      throw new Error('Only buyers can create orders');
    }

    return await Order.create({
      buyerId,
      ...orderData
    });
  }

  static async getOrder(orderId, userId, userRole) {
    const order = await Order.findById(orderId);
    if (!order) {
      throw new Error('Order not found');
    }

    // Check authorization
    if (userRole === 'buyer' && order.buyerId !== userId) {
      throw new Error('You can only view your own orders');
    }

    if (userRole === 'vendor') {
      // Check if vendor has items in this order
      const hasItems = order.items.some((item) => item.vendorId === userId);
      if (!hasItems) {
        throw new Error('You can only view orders containing your products');
      }

      // Return vendor-specific view
      return {
        ...order.toJSON(),
        vendorSummary: order.getVendorSummary(userId)
      };
    }

    return order;
  }

  static async getUserOrders(userId, userRole, options = {}) {
    if (userRole === 'buyer') {
      return await Order.findByUser(userId, options);
    } if (userRole === 'vendor') {
      return await Order.findByVendor(userId, options);
    }
    throw new Error('Invalid user role for order access');
  }

  static async updateOrderStatus(orderId, newStatus, userId, userRole) {
    const order = await Order.findById(orderId);
    if (!order) {
      throw new Error('Order not found');
    }

    // Authorization checks
    if (userRole === 'buyer') {
      if (order.buyerId !== userId) {
        throw new Error('You can only update your own orders');
      }

      // Buyers can only cancel orders
      if (newStatus !== 'cancelled') {
        throw new Error('Buyers can only cancel orders');
      }
    } else if (userRole === 'vendor') {
      // Vendors can only update orders containing their products
      const hasItems = order.items.some((item) => item.vendorId === userId);
      if (!hasItems) {
        throw new Error('You can only update orders containing your products');
      }

      // Vendors can confirm and mark as processing
      const allowedStatuses = ['confirmed', 'processing'];
      if (!allowedStatuses.includes(newStatus)) {
        throw new Error('Vendors can only confirm or mark orders as processing');
      }
    } else if (userRole !== 'admin') {
      throw new Error('Invalid user role for order updates');
    }

    return await order.updateStatus(newStatus);
  }

  static async cancelOrder(orderId, userId, userRole, reason = null) {
    const order = await Order.findById(orderId);
    if (!order) {
      throw new Error('Order not found');
    }

    // Check authorization
    if (userRole === 'buyer' && order.buyerId !== userId) {
      throw new Error('You can only cancel your own orders');
    }

    if (userRole === 'vendor') {
      // Vendors can cancel orders containing their products if not yet shipped
      const hasItems = order.items.some((item) => item.vendorId === userId);
      if (!hasItems) {
        throw new Error('You can only cancel orders containing your products');
      }
    }

    return await order.cancel(reason);
  }

  static async updatePaymentStatus(orderId, paymentStatus, paymentReference = null) {
    const order = await Order.findById(orderId);
    if (!order) {
      throw new Error('Order not found');
    }

    return await order.updatePaymentStatus(paymentStatus, paymentReference);
  }

  static async getOrderStats(userId, userRole, period = 'month') {
    let dateFilter = '';
    const now = new Date();

    if (period === 'week') {
      const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
      dateFilter = `AND o.created_at >= '${weekAgo.toISOString()}'`;
    } else if (period === 'month') {
      const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
      dateFilter = `AND o.created_at >= '${monthAgo.toISOString()}'`;
    }

    let query;
    let params;

    if (userRole === 'buyer') {
      query = `
        SELECT 
          COUNT(*) as total_orders,
          COUNT(CASE WHEN o.status = 'delivered' THEN 1 END) as completed_orders,
          COUNT(CASE WHEN o.status = 'cancelled' THEN 1 END) as cancelled_orders,
          COALESCE(SUM(o.total_amount), 0) as total_spent,
          COALESCE(AVG(o.total_amount), 0) as average_order_value
        FROM orders o
        WHERE o.buyer_id = $1 ${dateFilter}
      `;
      params = [userId];
    } else if (userRole === 'vendor') {
      query = `
        SELECT 
          COUNT(DISTINCT o.id) as total_orders,
          COUNT(DISTINCT CASE WHEN o.status = 'delivered' THEN o.id END) as completed_orders,
          COUNT(DISTINCT CASE WHEN o.status = 'cancelled' THEN o.id END) as cancelled_orders,
          COALESCE(SUM(oi.total_price), 0) as total_revenue,
          COALESCE(AVG(oi.total_price), 0) as average_order_value,
          COUNT(oi.id) as total_items_sold
        FROM orders o
        JOIN order_items oi ON o.id = oi.order_id
        WHERE oi.vendor_id = $1 ${dateFilter}
      `;
      params = [userId];
    } else {
      throw new Error('Invalid user role for stats');
    }

    const db = require('../config/database.config');
    const { rows } = await db.query(query, params);

    const stats = {
      totalOrders: parseInt(rows[0].total_orders),
      completedOrders: parseInt(rows[0].completed_orders),
      cancelledOrders: parseInt(rows[0].cancelled_orders),
      totalValue: parseFloat(rows[0].total_spent || rows[0].total_revenue),
      averageOrderValue: parseFloat(rows[0].average_order_value)
    };

    if (userRole === 'vendor') {
      stats.totalItemsSold = parseInt(rows[0].total_items_sold);
    }

    return stats;
  }

  static async getRecentOrders(userId, userRole, limit = 5) {
    const orders = await OrderService.getUserOrders(userId, userRole, { limit });

    return orders.map((order) => ({
      id: order.id,
      orderNumber: order.orderNumber,
      totalAmount: order.totalAmount,
      status: order.status,
      createdAt: order.createdAt,
      itemCount: order.items.length
    }));
  }

  // Admin functions
  static async getAllOrders(options = {}) {
    const {
      status = null,
      paymentStatus = null,
      page = 1,
      limit = 20
    } = options;

    const offset = (page - 1) * limit;
    let query = `
      SELECT o.*,
        JSON_BUILD_OBJECT(
          'id', u.id,
          'firstName', u.first_name,
          'lastName', u.last_name,
          'email', u.email
        ) as buyer
      FROM orders o
      JOIN users u ON o.buyer_id = u.id
      WHERE 1=1
    `;
    const values = [];
    let paramCount = 0;

    if (status) {
      paramCount++;
      query += ` AND o.status = $${paramCount}`;
      values.push(status);
    }

    if (paymentStatus) {
      paramCount++;
      query += ` AND o.payment_status = $${paramCount}`;
      values.push(paymentStatus);
    }

    query += ` ORDER BY o.created_at DESC LIMIT $${paramCount + 1} OFFSET $${paramCount + 2}`;
    values.push(limit, offset);

    const db = require('../config/database.config');
    const { rows } = await db.query(query, values);

    return rows.map((row) => ({
      ...new Order(row).toJSON(),
      buyer: row.buyer
    }));
  }
}

module.exports = OrderService;

const crypto = require('crypto');
const axios = require('axios');
const Payment = require('../models/payment.model');
const Order = require('../models/order.model');

class PaymentService {
  constructor() {
    this.orangeMoneyConfig = {
      mode: process.env.ORANGE_MONEY_MODE || 'development',
      apiUrl: process.env.ORANGE_MONEY_API_URL,
      username: process.env.ORANGE_MONEY_USERNAME,
      password: process.env.ORANGE_MONEY_PASSWORD,
      merchantNumber: process.env.ORANGE_MONEY_MERCHANT_NUMBER,
      webhookSecret: process.env.ORANGE_MONEY_WEBHOOK_SECRET,
      callbackUrl: process.env.ORANGE_MONEY_CALLBACK_URL,
      returnUrl: process.env.ORANGE_MONEY_RETURN_URL,
      cancelUrl: process.env.ORANGE_MONEY_CANCEL_URL
    };

    this.mockMode = process.env.PAYMENT_MOCK_MODE === 'true';
    this.autoConfirmDelay = parseInt(process.env.PAYMENT_AUTO_CONFIRM_DELAY, 10) || 5000;
  }

  // Initiate payment process
  async initiatePayment(paymentData) {
    const {
      orderId,
      paymentMethod,
      customerPhone,
      customerName,
      customerEmail,
      otpCode,
      userId,
      ipAddress,
      userAgent
    } = paymentData;

    // Get order details
    const order = await Order.findById(orderId);
    if (!order) {
      throw new Error('Order not found');
    }

    if (order.paymentStatus === 'paid') {
      throw new Error('Order is already paid');
    }

    // Validate payment method
    if (!this.isValidPaymentMethod(paymentMethod)) {
      throw new Error(`Invalid payment method: ${paymentMethod}`);
    }

    // Create payment record
    const payment = await Payment.create({
      orderId,
      paymentMethod,
      amount: order.totalAmount,
      currency: order.currency || 'XOF',
      customerPhone,
      customerName,
      customerEmail,
      returnUrl: this.orangeMoneyConfig.returnUrl,
      cancelUrl: this.orangeMoneyConfig.cancelUrl,
      createdBy: userId,
      ipAddress,
      userAgent
    });

    // Fraud detection
    const fraudCheck = payment.detectFraud();
    if (fraudCheck.recommendation === 'block') {
      await payment.updateStatus('failed', {
        errorDetails: { reason: 'fraud_detected', flags: fraudCheck.flags },
        updatedBy: userId
      });
      throw new Error('Payment blocked due to fraud detection');
    }

    // Process payment based on method
    let result;
    switch (paymentMethod) {
      case 'orange_money':
        result = await this.processOrangeMoneyPayment(payment, otpCode);
        break;
      case 'cash_on_delivery':
        result = await this.processCashOnDeliveryPayment(payment);
        break;
      case 'bank_transfer':
        result = await this.processBankTransferPayment(payment);
        break;
      default:
        throw new Error(`Payment method ${paymentMethod} not implemented`);
    }

    return result;
  }

  // Process Orange Money payment
  async processOrangeMoneyPayment(payment, otpCode) {
    try {
      if (this.mockMode) {
        return await this.processMockOrangeMoneyPayment(payment, otpCode);
      }

      // Real Orange Money API integration
      const paymentRequest = {
        merchant_key: this.orangeMoneyConfig.merchantNumber,
        currency: payment.currency,
        order_id: payment.paymentReference,
        amount: payment.amount,
        return_url: payment.returnUrl,
        cancel_url: payment.cancelUrl,
        notif_url: this.orangeMoneyConfig.callbackUrl,
        lang: 'fr',
        reference: payment.paymentReference,
        customer_msisdn: payment.customerPhone,
        customer_name: payment.customerName,
        customer_email: payment.customerEmail
      };

      // Add OTP if provided (for direct payment)
      if (otpCode) {
        paymentRequest.otp = otpCode;
      }

      const response = await this.makeOrangeMoneyRequest('POST', '/webpayment', paymentRequest);

      if (response.status === 'success') {
        // Update payment with Orange Money response
        await payment.updateStatus('processing', {
          gatewayResponse: response,
          externalTransactionId: response.transaction_id,
          paymentToken: response.payment_token
        });

        // Update payment URL if redirect is needed
        if (response.payment_url) {
          await payment.updatePaymentUrl(response.payment_url, response.payment_token);
        }

        return {
          success: true,
          paymentReference: payment.paymentReference,
          paymentUrl: response.payment_url,
          status: 'processing',
          message: 'Payment initiated successfully'
        };
      }
      await payment.updateStatus('failed', {
        errorDetails: response,
        gatewayResponse: response
      });

      throw new Error(response.message || 'Payment initiation failed');
    } catch (error) {
      await payment.updateStatus('failed', { errorDetails: { error: error.message } });
      throw error;
    }
  }

  // Mock Orange Money payment for development
  async processMockOrangeMoneyPayment(payment, otpCode) {
    if (process.env.NODE_ENV !== 'test') {
      // eslint-disable-next-line no-console
      console.log(`[MOCK] Processing Orange Money payment for ${payment.paymentReference}`);
    }

    // Simulate different scenarios based on phone number
    const lastDigit = payment.customerPhone.slice(-1);
    let mockResponse;

    if (lastDigit === '0') {
      // Simulate failure
      mockResponse = {
        status: 'error',
        message: 'Insufficient balance',
        error_code: 'INSUFFICIENT_BALANCE'
      };
    } else if (lastDigit === '9') {
      // Simulate OTP required
      if (!otpCode) {
        mockResponse = {
          status: 'otp_required',
          message: 'OTP code required',
          payment_token: `MOCK_TOKEN_${Date.now()}`
        };
      } else if (otpCode !== '1234') {
        mockResponse = {
          status: 'error',
          message: 'Invalid OTP code',
          error_code: 'INVALID_OTP'
        };
      } else {
        mockResponse = {
          status: 'success',
          transaction_id: `MOCK_TXN_${Date.now()}`,
          payment_token: `MOCK_TOKEN_${Date.now()}`,
          message: 'Payment successful'
        };
      }
    } else {
      // Simulate redirect flow
      mockResponse = {
        status: 'success',
        transaction_id: `MOCK_TXN_${Date.now()}`,
        payment_url: `${process.env.FRONTEND_URL}/mock-payment/${payment.paymentReference}`,
        payment_token: `MOCK_TOKEN_${Date.now()}`,
        message: 'Redirect to Orange Money'
      };
    }

    // Update payment based on mock response
    if (mockResponse.status === 'success') {
      await payment.updateStatus('processing', {
        gatewayResponse: mockResponse,
        externalTransactionId: mockResponse.transaction_id,
        paymentToken: mockResponse.payment_token
      });

      if (mockResponse.payment_url) {
        await payment.updatePaymentUrl(mockResponse.payment_url, mockResponse.payment_token);
      }

      // Auto-confirm after delay (simulate user completing payment)
      if (!mockResponse.payment_url) {
        setTimeout(async () => {
          try {
            await this.confirmMockPayment(payment.paymentReference);
          } catch (error) {
            console.error('Error auto-confirming mock payment:', error);
          }
        }, this.autoConfirmDelay);
      }
    } else if (mockResponse.status === 'otp_required') {
      await payment.updateStatus('processing', {
        gatewayResponse: mockResponse,
        paymentToken: mockResponse.payment_token
      });
    } else {
      await payment.updateStatus('failed', {
        errorDetails: mockResponse,
        gatewayResponse: mockResponse
      });
    }

    return {
      success: mockResponse.status !== 'error',
      paymentReference: payment.paymentReference,
      paymentUrl: mockResponse.payment_url,
      paymentToken: mockResponse.payment_token,
      status: mockResponse.status === 'success' ? 'processing' : mockResponse.status,
      message: mockResponse.message,
      requiresOtp: mockResponse.status === 'otp_required'
    };
  }

  // Process Cash on Delivery payment
  async processCashOnDeliveryPayment(payment) {
    // COD payments are confirmed manually by delivery driver
    await payment.updateStatus('processing', { gatewayResponse: { method: 'cash_on_delivery', status: 'pending_delivery' } });

    return {
      success: true,
      paymentReference: payment.paymentReference,
      status: 'processing',
      message: 'Cash on delivery payment registered. Payment will be collected upon delivery.'
    };
  }

  // Process Bank Transfer payment
  async processBankTransferPayment(payment) {
    // Generate bank transfer details
    const transferDetails = {
      accountName: 'E-Commerce Platform',
      accountNumber: '0123456789',
      bankName: 'Ecobank Burkina Faso',
      swiftCode: 'ECOCBFBF',
      reference: payment.paymentReference,
      amount: payment.amount,
      currency: payment.currency
    };

    await payment.updateStatus('processing', { gatewayResponse: { method: 'bank_transfer', transferDetails } });

    return {
      success: true,
      paymentReference: payment.paymentReference,
      status: 'processing',
      transferDetails,
      message: 'Please make the bank transfer using the provided details'
    };
  }

  // Handle webhook notifications
  async handleWebhook(webhookData, signature, ipAddress, userAgent) {
    // Validate webhook signature
    if (!this.validateWebhookSignature(webhookData, signature)) {
      throw new Error('Invalid webhook signature');
    }

    const { reference, status, transaction_id, error_message } = webhookData;

    // Find payment by reference
    const payment = await Payment.findByReference(reference);
    if (!payment) {
      throw new Error(`Payment not found for reference: ${reference}`);
    }

    // Log webhook data
    console.log(`[WEBHOOK] Received notification for ${reference}: ${status}`);

    const updateData = {
      webhookData,
      ipAddress,
      userAgent
    };

    // Update payment status based on webhook
    switch (status.toLowerCase()) {
      case 'success':
      case 'completed':
        if (payment.status !== 'completed') {
          await payment.updateStatus('completed', {
            ...updateData,
            externalTransactionId: transaction_id,
            authorizationCode: webhookData.authorization_code
          });

          // Update order payment status
          const order = await Order.findById(payment.orderId);
          if (order) {
            await order.updatePaymentStatus('paid', payment.paymentReference);
          }
        }
        break;

      case 'failed':
      case 'error':
        if (payment.status === 'processing') {
          await payment.updateStatus('failed', {
            ...updateData,
            errorDetails: { error_message, webhook_data: webhookData }
          });
        }
        break;

      case 'cancelled':
        if (['pending', 'processing'].includes(payment.status)) {
          await payment.updateStatus('cancelled', updateData);
        }
        break;

      default:
        console.warn(`[WEBHOOK] Unknown status: ${status} for ${reference}`);
    }

    return { success: true, processed: true };
  }

  // Verify payment status
  async verifyPayment(paymentReference) {
    const payment = await Payment.findByReference(paymentReference);
    if (!payment) {
      throw new Error('Payment not found');
    }

    if (this.mockMode) {
      return this.verifyMockPayment(payment);
    }

    try {
      // Query Orange Money API for payment status
      const response = await this.makeOrangeMoneyRequest('GET', `/webpayment/${paymentReference}`);

      // Update payment status based on API response
      const updateData = { gatewayResponse: response };

      switch (response.status?.toLowerCase()) {
        case 'success':
        case 'completed':
          if (payment.status !== 'completed') {
            await payment.updateStatus('completed', {
              ...updateData,
              externalTransactionId: response.transaction_id
            });
          }
          break;

        case 'failed':
        case 'error':
          if (payment.status === 'processing') {
            await payment.updateStatus('failed', {
              ...updateData,
              errorDetails: response
            });
          }
          break;

        case 'pending':
        case 'processing':
          // Status hasn't changed, just update gateway response
          break;

        default:
          console.warn(`[VERIFY] Unknown status: ${response.status} for ${paymentReference}`);
      }

      return payment.toJSON();
    } catch (error) {
      console.error(`[VERIFY] Error verifying payment ${paymentReference}:`, error);
      throw new Error('Payment verification failed');
    }
  }

  // Mock payment verification
  async verifyMockPayment(payment) {
    // Simulate different verification results
    const shouldSucceed = Math.random() > 0.1; // 90% success rate

    if (shouldSucceed && payment.status === 'processing') {
      await payment.updateStatus('completed', {
        externalTransactionId: `MOCK_VERIFIED_${Date.now()}`,
        gatewayResponse: { verified: true, mock: true }
      });

      // Update order payment status
      const order = await Order.findById(payment.orderId);
      if (order) {
        await order.updatePaymentStatus('paid', payment.paymentReference);
      }
    }

    return payment.toJSON();
  }

  // Confirm mock payment (for testing)
  async confirmMockPayment(paymentReference) {
    const payment = await Payment.findByReference(paymentReference);
    if (!payment) {
      throw new Error('Payment not found');
    }

    if (payment.status === 'processing') {
      await payment.updateStatus('completed', {
        externalTransactionId: `MOCK_CONFIRMED_${Date.now()}`,
        gatewayResponse: { confirmed: true, mock: true }
      });

      // Update order payment status
      const order = await Order.findById(payment.orderId);
      if (order) {
        await order.updatePaymentStatus('paid', payment.paymentReference);
      }

      console.log(`[MOCK] Auto-confirmed payment: ${paymentReference}`);
    }

    return payment.toJSON();
  }

  // Make request to Orange Money API
  async makeOrangeMoneyRequest(method, endpoint, data = null) {
    const url = `${this.orangeMoneyConfig.apiUrl}${endpoint}`;
    const headers = {
      'Content-Type': 'application/json',
      Authorization: this.generateOrangeMoneyAuth()
    };

    const config = {
      method,
      url,
      headers,
      timeout: 30000
    };

    if (data) {
      config.data = data;
    }

    try {
      const response = await axios(config);
      return response.data;
    } catch (error) {
      if (error.response) {
        throw new Error(`Orange Money API error: ${error.response.data?.message || error.response.statusText}`);
      } else if (error.request) {
        throw new Error('Orange Money API is not responding');
      } else {
        throw new Error(`Request error: ${error.message}`);
      }
    }
  }

  // Generate Orange Money authentication
  generateOrangeMoneyAuth() {
    const credentials = `${this.orangeMoneyConfig.username}:${this.orangeMoneyConfig.password}`;
    return `Basic ${Buffer.from(credentials).toString('base64')}`;
  }

  // Validate webhook signature
  validateWebhookSignature(payload, signature) {
    if (!this.orangeMoneyConfig.webhookSecret) {
      console.warn('[WEBHOOK] No webhook secret configured, skipping signature validation');
      return true; // Allow for development
    }

    const expectedSignature = crypto
      .createHmac('sha256', this.orangeMoneyConfig.webhookSecret)
      .update(JSON.stringify(payload))
      .digest('hex');

    return crypto.timingSafeEqual(
      Buffer.from(signature, 'hex'),
      Buffer.from(expectedSignature, 'hex')
    );
  }

  // Validate payment method
  isValidPaymentMethod(method) {
    const validMethods = ['orange_money', 'cash_on_delivery', 'bank_transfer'];
    return validMethods.includes(method);
  }

  // Get payment statistics
  async getPaymentStatistics(options = {}) {
    return await Payment.getStatistics(options);
  }

  // Clean up expired payments
  async cleanupExpiredPayments() {
    return await Payment.cleanupExpiredPayments();
  }

  // Refund payment
  async refundPayment(paymentReference, refundAmount = null, reason = null) {
    const payment = await Payment.findByReference(paymentReference);
    if (!payment) {
      throw new Error('Payment not found');
    }

    if (payment.status !== 'completed') {
      throw new Error('Only completed payments can be refunded');
    }

    // In a real implementation, you would call the Orange Money refund API
    if (!this.mockMode) {
      // TODO: Implement actual Orange Money refund API call
      console.log(`[REFUND] Would initiate refund for ${paymentReference}`);
    }

    await payment.refund(refundAmount, reason);

    // Update order status if needed
    const order = await Order.findById(payment.orderId);
    if (order) {
      await order.updatePaymentStatus('refunded', payment.paymentReference);
    }

    return payment.toJSON();
  }
}

module.exports = new PaymentService();

const Product = require('../models/product.model');
const User = require('../models/user.model');

class ProductService {
  static async createProduct(vendorId, productData) {
    // Verify vendor can manage products
    const vendor = await User.findById(vendorId);
    if (!vendor || !vendor.canManageProducts()) {
      throw new Error('Only active vendors can create products');
    }

    // Validate required fields
    const { title, price, category } = productData;
    if (!title || !price || !category) {
      throw new Error('Title, price, and category are required');
    }

    if (price <= 0) {
      throw new Error('Price must be greater than 0');
    }

    return Product.create({
      vendorId,
      ...productData
    });
  }

  static async getProduct(id, includeVendor = false) {
    const product = await Product.findById(id);
    if (!product) {
      throw new Error('Product not found');
    }

    if (includeVendor) {
      const vendor = await User.findById(product.vendorId);
      return {
        ...product.toJSON(),
        vendor: vendor ? {
          id: vendor.id,
          businessName: vendor.businessName,
          firstName: vendor.firstName,
          lastName: vendor.lastName,
          verification: vendor.verification
        } : null
      };
    }

    return product;
  }

  static async updateProduct(id, vendorId, updates) {
    const product = await Product.findById(id);
    if (!product) {
      throw new Error('Product not found');
    }

    // Check ownership
    if (product.vendorId !== vendorId) {
      throw new Error('You can only update your own products');
    }

    // Validate price if being updated
    if (updates.price !== undefined && updates.price <= 0) {
      throw new Error('Price must be greater than 0');
    }

    return await product.update(updates);
  }

  static async deleteProduct(id, vendorId) {
    const product = await Product.findById(id);
    if (!product) {
      throw new Error('Product not found');
    }

    // Check ownership
    if (product.vendorId !== vendorId) {
      throw new Error('You can only delete your own products');
    }

    return await product.delete();
  }

  static async getProducts(options = {}) {
    const {
      page = 1,
      limit = 20,
      ...filterOptions
    } = options;

    const offset = (page - 1) * limit;
    const products = await Product.findAll({
      ...filterOptions,
      limit: Math.min(limit, 100), // Cap at 100
      offset
    });

    const total = await Product.count(filterOptions);
    const totalPages = Math.ceil(total / limit);

    return {
      products,
      pagination: {
        page: parseInt(page),
        limit: parseInt(limit),
        total,
        totalPages,
        hasNext: page < totalPages,
        hasPrev: page > 1
      }
    };
  }

  static async getVendorProducts(vendorId, options = {}) {
    const {
      page = 1,
      limit = 20,
      status = null
    } = options;

    const offset = (page - 1) * limit;
    const products = await Product.findByVendor(vendorId, {
      status,
      limit: Math.min(limit, 100),
      offset
    });

    // Count total for pagination
    const countQuery = status
      ? { vendorId, status }
      : { vendorId };
    const total = await Product.count(countQuery);
    const totalPages = Math.ceil(total / limit);

    return {
      products,
      pagination: {
        page: parseInt(page),
        limit: parseInt(limit),
        total,
        totalPages,
        hasNext: page < totalPages,
        hasPrev: page > 1
      }
    };
  }

  static async searchProducts(searchTerm, options = {}) {
    return await this.getProducts({
      search: searchTerm,
      status: 'active',
      ...options
    });
  }

  static async getProductsByCategory(category, options = {}) {
    return await this.getProducts({
      category,
      status: 'active',
      ...options
    });
  }

  static async getProductsByTags(tags, options = {}) {
    return await this.getProducts({
      tags: Array.isArray(tags) ? tags : [tags],
      status: 'active',
      ...options
    });
  }

  static async getFeaturedProducts(limit = 10) {
    // For now, return newest active products
    // In the future, this could be based on sales, ratings, etc.
    return await Product.findAll({
      status: 'active',
      sortBy: 'created_at',
      sortOrder: 'DESC',
      limit
    });
  }

  static async getCategories() {
    return await Product.getCategories();
  }

  static async getTags() {
    return await Product.getTags();
  }

  static async adjustInventory(productId, quantity, operation = 'decrease') {
    const product = await Product.findById(productId);
    if (!product) {
      throw new Error('Product not found');
    }

    return await product.adjustInventory(quantity, operation);
  }

  static async checkAvailability(productId, requestedQuantity) {
    const product = await Product.findById(productId);
    if (!product) {
      throw new Error('Product not found');
    }

    if (product.status !== 'active') {
      return {
        available: false,
        reason: 'Product is not active',
        availableQuantity: 0
      };
    }

    if (!product.trackInventory) {
      return {
        available: true,
        availableQuantity: null // Unlimited
      };
    }

    const available = product.quantity >= requestedQuantity || product.allowBackorder;

    return {
      available,
      availableQuantity: product.quantity,
      reason: available ? null : 'Insufficient inventory'
    };
  }

  static async getProductStats(vendorId) {
    const query = `
      SELECT 
        COUNT(CASE WHEN status = 'active' THEN 1 END) as active_products,
        COUNT(CASE WHEN status = 'draft' THEN 1 END) as draft_products,
        COUNT(CASE WHEN status = 'archived' THEN 1 END) as archived_products,
        COUNT(*) as total_products,
        COALESCE(AVG(price), 0) as average_price,
        COALESCE(SUM(quantity), 0) as total_inventory
      FROM products 
      WHERE vendor_id = $1
    `;

    const db = require('../config/database.config');
    const { rows } = await db.query(query, [vendorId]);

    return {
      activeProducts: parseInt(rows[0].active_products),
      draftProducts: parseInt(rows[0].draft_products),
      archivedProducts: parseInt(rows[0].archived_products),
      totalProducts: parseInt(rows[0].total_products),
      averagePrice: parseFloat(rows[0].average_price),
      totalInventory: parseInt(rows[0].total_inventory)
    };
  }
}

module.exports = ProductService;
